// variable to hold an instance of the p5.webserial library:
const serial = new p5.WebSerial();

// port chooser button:
let portButton;
let sensor1 = 0
//let sensor2 = 0

// CUSTOMIZE: change/add variable for incoming serial data:
let inData;

// Variables for bird UI
let oxygenBar
let startOxygen = 50

let birdImg
let backgroundImg
let o2Img
let startscreenImg
let endscreenImg
let showStartScreen = true

const o2ImgHeight = 200
let isAddingOx = false
let timer
let gameIsEnd = false

function setup() {
   // check to see if serial is available:
   if (!navigator.serial) {
    alert("WebSerial is not supported in this browser. Try Chrome or MS Edge.");
  } else {
    alert("WebSerial is supported. Enjoy!");
  }
  // check to see if serial is available:
   if (!navigator.serial) {
    alert("WebSerial is not supported in this browser. Try Chrome or MS Edge.");
  }
  // check for any ports that are available:
  serial.getPorts();
  // if there's no port chosen, choose one:
  serial.on("noport", makePortButton);
  // open whatever port is available:
  serial.on("portavailable", openPort);
  // handle serial errors:
  serial.on("requesterror", portError);
  // handle any incoming serial data:
  serial.on("data", serialEvent);
  serial.on("close", makePortButton)

  // add serial connect/disconnect listeners from WebSerial API:
  navigator.serial.addEventListener("connect", portConnect);
  navigator.serial.addEventListener("disconnect", portDisconnect);
  createCanvas(windowWidth, windowHeight);
  oxygenBar = new OxygenBar(width/2 - 150, 280 + o2ImgHeight, 50, startOxygen)
  birdImg = loadImage('images/bird.png')
  backgroundImg = loadImage('images/mining.png')
  o2Img = loadImage('images/o2bar.png'); 
  startscreenImg = loadImage('images/startscreen.png')
  endscreenImg = loadImage('images/endscreen.png')
}

// if there's no port selected, 
// make a port select button appear:
function makePortButton() {
  // create and position a port chooser button:
  portButton = createButton('choose port');
  portButton.position(10, 10);
  // give the port button a mousepressed handler:
  portButton.mousePressed(choosePort);
}

// make the port selector window appear:
function choosePort() {
  if (portButton) portButton.show();
  serial.requestPort();
}

// open the selected port, and make the port 
// button invisible:
function openPort() {
  // wait for the serial.open promise to return,
  // then call the initiateSerial function
  let options = { baudrate: 9600}; 
  serial.open().then(initiateSerial);

  // once the port opens, let the user know:
  function initiateSerial() {
    console.log("port open");
  }
  // hide the port button once a port is chosen:
  if (portButton) portButton.hide();
}

// pop up an alert if there's a port error:
function portError(err) {
  alert("Serial port error: " + err);
}
// read any incoming data as a string
// (assumes a newline at the end of it):
function serialEvent() {

  // CUSTOMIZE: add your code for receiving/sending data over serial  
  let stringFromSerial = serial.readString()
  if (stringFromSerial != null) {
    let value_array = stringFromSerial.trim().split(',')
    //console.log(value_array[0])
    //sensor1 = Number(stringFromSerial)
    sensor1 = int(value_array[0])
    showStartScreen = sensor1 === 1 ? false : true
    isAddingOx = sensor1 === 1
    if (timer) {
      clearTimeout(timer)
    }
    timer = setTimeout(()=> {
      isAddingOx = false
    }, 1000)
    //sensor2 = int(value_array[1])
  }
   //serial.print(oxygenBar.barHeight)

}

// try to connect if a new serial port 
// gets added (i.e. plugged in via USB):
function portConnect() {
  console.log("port connected");
  serial.getPorts();
}

// if a port is disconnected:
function portDisconnect() {
  serial.close();
  console.log("port disconnected");
}

function closePort() {
  serial.close();
}

function mousePressed() {
  serial.print(oxygenBar.barHeight)
}


function draw() {
  background(220);
  //image(birdImg,0,0)
  image(backgroundImg,0,0, windowWidth, windowHeight)
  image(o2Img,width/2 - 200, 280, 150,o2ImgHeight)

  // CUSTOMIZE: add your code for receiving/sending data over serial  

  oxygenBar.show()
  oxygenBar.update()
  //console.log('sensor',sensor1)



  if(isAddingOx) {

    //ellipse(width/2, height/2, 50)
    oxygenBar.addOxygen()
  }
    serial.write(oxygenBar.barHeight)
  showStartScreen = oxygenBar.barHeight <= 10 ? true:false

  if (showStartScreen) {
    push()
    imageMode(CENTER)
    image(startscreenImg, width/2, height/2, 250,250)
    pop()
  }

  if (oxygenBar.barHeight >= 150) {
    push()
    imageMode(CENTER)
    image(endscreenImg, width/2, height/2, 250,250)
    pop()
  } 

}

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

class OxygenBar {
  constructor(posX, posY, barWidth, barHeight) {
    this.posX = posX
    this.posY = posY
    this.barWidth = barWidth
    this.barHeight = barHeight
  }

  update() {
    if (this.barHeight >= 160) this.barHeight = 160
    if (this.barHeight <= 0)  this.barHeight = 0
    if(this.barHeight >0)this.barHeight -= 0.5
  }

  addOxygen() {
    //if (this.barHeight >= 160) this.barHeight = 160
    //if (this.barHeight <= 0)  this.barHeight = 0
    this.barHeight += 2
  }

  show() {
    push()
    translate(this.posX, this.posY)
    scale(1, -1)
    fill('#ffffff')
    rect(0, 0, this.barWidth, this.barHeight)
    pop()
  }


}

/*
Serial communitaion with Arduino 
by Yeseul for Physical Computing, 2022

This is a boiler plate for using webserial.
Duplicat this sketch and start adding your code to "CUSTOMIZE" part 
to make your serial communication project to use with your Arduino sketch.


The serial communication part is based on 
https://itp.nyu.edu/physcomp/labs/labs-serial-communication/lab-webserial-input-to-p5-js/
*/

// variable to hold an instance of the p5.webserial library:
const serial = new p5.WebSerial();

// port chooser button:
let portButton;
let sensor1 = 0;
//let sensor2 = 0

// CUSTOMIZE: change/add variable for incoming serial data:
let inData;

// Variables for bird UI
let oxygenBar;
let startOxygen = 50;

let birdImg;
let backgroundImg;
let o2Img;
let startscreenImg;
let endscreenImg;
let showStartScreen = true;

const o2ImgHeight = 200;
let isAddingOx = false;
let timer;
let gameIsEnd = false;
let endText = `Canary resuscitators were used in late 1800s \n by miners to carry canaries to detect gases in mines \n because they are more sensitive to rising CO2 levels.\n The resuscitators were used to \n keep the canaries alive.\n More Info:\n https://blog.scienceandindustrymuseum.org.uk/\ncanary-resuscitator/`

function setup() {
  // check to see if serial is available:
  if (!navigator.serial) {
    alert("WebSerial is not supported in this browser. Try Chrome or MS Edge.");
  } else {
    alert("WebSerial is supported. Enjoy!");
  }
  // check to see if serial is available:
  if (!navigator.serial) {
    alert("WebSerial is not supported in this browser. Try Chrome or MS Edge.");
  }
  // check for any ports that are available:
  serial.getPorts();
  // if there's no port chosen, choose one:
  serial.on("noport", makePortButton);
  // open whatever port is available:
  serial.on("portavailable", openPort);
  // handle serial errors:
  serial.on("requesterror", portError);
  // handle any incoming serial data:
  serial.on("data", serialEvent);
  serial.on("close", makePortButton);

  // add serial connect/disconnect listeners from WebSerial API:
  navigator.serial.addEventListener("connect", portConnect);
  navigator.serial.addEventListener("disconnect", portDisconnect);
  createCanvas(windowWidth, windowHeight);
  oxygenBar = new OxygenBar(
    width / 2 - 150,
    280 + o2ImgHeight,
    50,
    startOxygen
  );
  birdImg = loadImage("images/bird.png");
  backgroundImg = loadImage("images/mining.png");
  o2Img = loadImage("images/o2bar.png");
  startscreenImg = loadImage("images/startscreen.png");
  endscreenImg = loadImage("images/endscreen.png");
}

// if there's no port selected,
// make a port select button appear:
function makePortButton() {
  // create and position a port chooser button:
  portButton = createButton("choose port");
  portButton.position(10, 10);
  // give the port button a mousepressed handler:
  portButton.mousePressed(choosePort);
}

// make the port selector window appear:
function choosePort() {
  if (portButton) portButton.show();
  serial.requestPort();
}

// open the selected port, and make the port
// button invisible:
function openPort() {
  // wait for the serial.open promise to return,
  // then call the initiateSerial function
  let options = { baudrate: 9600 };
  serial.open().then(initiateSerial);

  // once the port opens, let the user know:
  function initiateSerial() {
    console.log("port open");
  }
  // hide the port button once a port is chosen:
  if (portButton) portButton.hide();
}

// pop up an alert if there's a port error:
function portError(err) {
  alert("Serial port error: " + err);
}
// read any incoming data as a string
// (assumes a newline at the end of it):
function serialEvent() {
  // CUSTOMIZE: add your code for receiving/sending data over serial
  let stringFromSerial = serial.readString();
  if (stringFromSerial != null) {
    let value_array = stringFromSerial.trim().split(",");
    //console.log(value_array[0])
    //sensor1 = Number(stringFromSerial)
    sensor1 = int(value_array[0]);
    showStartScreen = sensor1 === 1 ? false : true;
    isAddingOx = sensor1 === 1;
    if (timer) {
      clearTimeout(timer);
    }
    timer = setTimeout(() => {
      isAddingOx = false;
    }, 1000);
    //sensor2 = int(value_array[1])
  }
  //serial.print(oxygenBar.barHeight)
}

// try to connect if a new serial port
// gets added (i.e. plugged in via USB):
function portConnect() {
  console.log("port connected");
  serial.getPorts();
}

// if a port is disconnected:
function portDisconnect() {
  serial.close();
  console.log("port disconnected");
}

function closePort() {
  serial.close();
}

function mousePressed() {
  serial.print(oxygenBar.barHeight);
}

function draw() {
  background(220);
  //image(birdImg,0,0)
  image(backgroundImg, 0, 0, windowWidth, windowHeight);
  image(o2Img, width / 2 - 200, 280, 150, o2ImgHeight);

  // CUSTOMIZE: add your code for receiving/sending data over serial
  if (gameIsEnd) {
    oxygenBar.barHeight = 150
    serial.write(oxygenBar.barHeight);
    oxygenBar.show();
    push();
    imageMode(CENTER);
    image(endscreenImg, width / 2, height / 2, 350, 350);
    pop();
    push()
    textSize(14)
    textAlign(CENTER)
    fill(255)
    text(endText, width/2, height/2)
    pop()
  }
  if (!gameIsEnd) {
    oxygenBar.show();
    oxygenBar.update();

    if (isAddingOx) {
      //ellipse(width/2, height/2, 50)
      oxygenBar.addOxygen();
    }
    serial.write(oxygenBar.barHeight);
    showStartScreen = oxygenBar.barHeight <= 10 ? true : false;

    if (showStartScreen) {
      push();
      imageMode(CENTER);
      image(startscreenImg, width / 2, height / 2, 250, 250);
      pop();
    }

    if (oxygenBar.barHeight >= 150) {
      push();
      imageMode(CENTER);
      image(endscreenImg, width / 2, height / 2, 250, 250);
      pop();
      gameIsEnd = true
    }
  }

  //console.log('sensor',sensor1)
}

html, body {
  margin: 0;
  padding: 0;
}
canvas {
  display: block;
}

class OxygenBar {
  constructor(posX, posY, barWidth, barHeight) {
    this.posX = posX
    this.posY = posY
    this.barWidth = barWidth
    this.barHeight = barHeight
  }

  update() {
    if (this.barHeight >= 160) this.barHeight = 160
    if (this.barHeight <= 0)  this.barHeight = 0
    if(this.barHeight >0)this.barHeight -= 0.5
  }

  addOxygen() {
    //if (this.barHeight >= 160) this.barHeight = 160
    //if (this.barHeight <= 0)  this.barHeight = 0
    this.barHeight += 3
  }

  show() {
    push()
    translate(this.posX, this.posY)
    scale(1, -1)
    fill('#ffffff')
    rect(0, 0, this.barWidth, this.barHeight)
    pop()
  }


}

let handles = [[90,190], [10,110]];
let handleClicked;
let handleD = 12;

let points = [[15, 180], [85, 120]];
let pointClicked;

//let beziers = [ [points[0], handles[0], handles[1], points[1]]];
let beziers = 1;

let editMode = false;
let showImage = false;
let tangent= false;


let crtlPressed = false;

let w = 800;
let h = 600;

// const preview = document.querySelector('.preview');


let bg;

// function preload() {
//   bg = loadImage("nurse.jpg");
//   console.log(bg)
// }

function setup() {
  //console.log(bg.height)
  // w = bg.width/5;
  // h = bg.height/5;
  // input = createFileInput(handleFile)
  w = 700
  h = 450
  createCanvas(w,h);
  enableEdit();
  toggleTangent();
  backImage();
  backImage();
}

function draw() {
  if(showImage && bg){
    // console.log(bg);
    if(bg.height && bg.height !== height){
      resizeCanvas(bg.width,bg.height)
      w = bg.width
      h = bg.height
    }
    background(bg);
  } else {
    background(200);
  }
  noFill();
  stroke(255, 102, 0);
  strokeWeight(1);
  //stroke(0, 0, 0);
  for(let i = 0; i < beziers; i++){
    let j = i*2;
    let b = [points[j], handles[j], handles[j+1], points[j+1]];
    //console.log(b)
    strokeWeight(round(document.querySelector(".slider").value/10));
 stroke(document.querySelector(".redSlider").value, document.querySelector(".greenSlider").value, document.querySelector(".blueSlider").value)
    bezier(b[0][0], b[0][1], b[1][0], b[1][1], b[2][0], b[2][1], b[3][0], b[3][1]);
    if(editMode) {
      strokeWeight(1)
      stroke(255)
      line(b[0][0], b[0][1], b[1][0], b[1][1]);
      line(b[2][0], b[2][1], b[3][0], b[3][1]);
    }
  }
  if(editMode) interactive();


}

function mousePressed() {

  // Ensure that the mouse was clicked within the canvas and editMode is enabled
  if(mouseX > 0 && mouseX < w && mouseY > 0 && mouseY < h && editMode){
    // Check to see if a handle was clicked
    for(let [i,p] of handles.entries()){
      // console.log("The "+ i + "th point " + p + " is "+ p[0]+"," +p[1])
      if(dist(p[0],p[1],mouseX,mouseY) <= handleD/2){
        handleClicked = i+1;
        // console.log(handleClicked)
        break;
      } else {
        handleClicked = false;
      }
    }

    // Check to see if a point was clicked
    for(let [i,p] of points.entries()){
      if(dist(p[0],p[1],mouseX,mouseY) <= handleD/2){
        pointClicked = i+1;
        // console.log(handleClicked)
        break;
      } else {
        pointClicked = false;
      }
    }

    if(handleClicked == false && pointClicked === false){
      beziers += 1;
      let th = atan2(points[points.length-1][1]-handles[handles.length-1][1], points[points.length-1][0]-handles[handles.length-1][0] )
      points.push([points[points.length-1][0], points[points.length-1][1]]);
      points.push([mouseX, mouseY]);
      handles.push([points[points.length-2][0] + 50*cos(th), points[points.length-2][1] + 50 *sin(th)]);
      th = atan2(handles[handles.length-1][1]-mouseY, handles[handles.length-1][0]-mouseX )
      handles.push([mouseX + 50*cos(th), mouseY+50*sin(th)]);
    }
  }
}

function mouseReleased() {
  handleClicked = false;
  pointClicked = false;
}

function keyPressed() {
  if(keyCode === CONTROL){
    ctrlPressed = true;
  }
  if(keyCode === 90 && keyIsDown(CONTROL) && points.length > 2){
    console.log(points.length);
    points.pop();
    points.pop();
    handles.pop();
    handles.pop();
    beziers -= 1;
  }
}

function keyReleased() {
  if(keyCode === CONTROL){
    ctrlPressed = false;
  }
}

function enableEdit() {
  editMode = !editMode;
  document.getElementById("edit").style.color = !editMode ?  "black" : "white";
  document.getElementById("edit").style.backgroundColor = !editMode ? "lightgray" : "gray";
}

function backImage() {
  showImage = !showImage;
  document.getElementById("image").style.color = !showImage ?  "black" : "white";
  document.getElementById("image").style.backgroundColor = !showImage ? "lightgray" : "gray";
}

function toggleTangent() {
  tangent = !tangent;
  document.getElementById("tangent").style.color = !tangent ?  "black" : "white";
  document.getElementById("tangent").style.backgroundColor = !tangent ? "lightgray" : "gray";
}

function interactive() {
  for(let [i,p] of points.entries()){
    if(i === pointClicked-1 & mouseIsPressed){
      fill(0,255,255);
    } else {
      noFill();
    }

    if(dist(p[0],p[1],mouseX,mouseY) <= handleD/2){
      stroke(0);
      strokeWeight(1.175);
    } else {
      stroke(30,120,230)
      strokeWeight(1);
    }

    circle(p[0], p[1], handleD);
  }

  fill(255);
  for(let [i,p] of handles.entries()){
    if(i === handleClicked-1 & mouseIsPressed){
      fill(250,90,60);
    } else {
      noFill();
    }

    if(dist(p[0],p[1],mouseX,mouseY) <= handleD/2){
      stroke(0);
      strokeWeight(1.175)
    } else {
      stroke(250,90,60)
      ;
      strokeWeight(1);
    }

    circle(p[0], p[1], handleD);
  }


  if(handleClicked && mouseIsPressed){
    handles[handleClicked-1][0] = mouseX > 0 && mouseX < w ? mouseX : mouseX > 0 ? w : 0;
    handles[handleClicked-1][1] = mouseY > 0 && mouseY < h ? mouseY : mouseY > 0 ? h : 0;

    if((handleClicked-1)%2 === 0 && handleClicked-1 > 1 && tangent) {
      let th = atan2(handles[handleClicked-1][1]-points[handleClicked-1][1], handles[handleClicked-1][0]-points[handleClicked-1][0]);
      let prevHandleMag = dist(handles[handleClicked-2][0], handles[handleClicked-2][1], points[handleClicked-1][0], points[handleClicked-1][1]);
      handles[handleClicked-2][0] = points[handleClicked-1][0] - prevHandleMag*cos(th);
      handles[handleClicked-2][1] = points[handleClicked-1][1] - prevHandleMag*sin(th);
    }
    if((handleClicked-1)%2 === 1 && handleClicked-1 > 0 && handleClicked !== handles.length && tangent) {
      let th = atan2(handles[handleClicked-1][1]-points[handleClicked][1], handles[handleClicked-1][0]-points[handleClicked][0]);
      let nextHandleMag = dist(handles[handleClicked][0], handles[handleClicked][1], points[handleClicked][0], points[handleClicked][1]);
      handles[handleClicked][0] = points[handleClicked][0] - nextHandleMag*cos(th);
      handles[handleClicked][1] = points[handleClicked][1] - nextHandleMag*sin(th);
    }
  }

  else if(pointClicked && mouseIsPressed){
    // console.log("Dragging")
    let h1dx = points[pointClicked-1][0] - handles[pointClicked-1][0];
    let h1dy = points[pointClicked-1][1] - handles[pointClicked-1][1];
    points[pointClicked-1][0] = mouseX > 0 && mouseX < w ? mouseX : mouseX > 0 ? w : 0;
    points[pointClicked-1][1] = mouseY > 0 && mouseY < h ? mouseY : mouseY > 0 ? h : 0;
    handles[pointClicked-1][0] = points[pointClicked-1][0] - h1dx;
    handles[pointClicked-1][1] = points[pointClicked-1][1] - h1dy;

    if((pointClicked-1)%2 === 0 && pointClicked-1){

      let h2dx = points[pointClicked][0] - handles[pointClicked][0];
      let h2dy = points[pointClicked][1] - handles[pointClicked][1];

      points[pointClicked-2][0] = mouseX > 0 && mouseX < w ? mouseX : mouseX > 0 ? w : 0;
      points[pointClicked-2][1] = mouseY > 0 && mouseY < h ? mouseY : mouseY > 0 ? h : 0;

      handles[pointClicked][0] = points[pointClicked][0] - h2dx;
      handles[pointClicked][1] = points[pointClicked][1] - h2dy;

    } 
    if((pointClicked-1)%2 === 1 && points[pointClicked]){

      let h2dx = points[pointClicked][0] - handles[pointClicked][0];
      let h2dy = points[pointClicked][1] - handles[pointClicked][1];

      points[pointClicked][0] = mouseX > 0 && mouseX < w ? mouseX : mouseX > 0 ? w : 0;
      points[pointClicked][1] = mouseY > 0 && mouseY < h ? mouseY : mouseY > 0 ? h : 0;

      handles[pointClicked][0] = points[pointClicked][0] - h2dx;
      handles[pointClicked][1] = points[pointClicked][1] - h2dy;
    }

  }
}



