// Flocking
// Daniel Shiffman
// https://thecodingtrain.com/CodingChallenges/124-flocking-boids.html
// https://youtu.be/mhjuuHl6qHM

class Boid {
  constructor() {
    this.position = createVector(random(width), random(height));
    this.velocity = p5.Vector.random2D();
    this.velocity.setMag(random(2, 5));
    this.acceleration = createVector();
    this.maxForce = 0.2;
    this.maxSpeed = 3;
		this.r = 4;
  }

  edges() {
    if (this.position.x > width) {
      this.position.x = 0;
    } else if (this.position.x < 0) {
      this.position.x = width;
    }
    if (this.position.y > height) {
      this.position.y = 0;
    } else if (this.position.y < 0) {
      this.position.y = height;
    }
  }

  align(boids) {
    let perceptionRadius = 10//25;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        steering.add(other.velocity);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

  separation(boids) {
    let perceptionRadius = 60//24;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        let diff = p5.Vector.sub(this.position, other.position);
        diff.div(d * d);
        steering.add(diff);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

  cohesion(boids) {
    let perceptionRadius = 20//50;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        steering.add(other.position);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.sub(this.position);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

	attractCenter() {
		const centerVec = createVector(width/2, height/2)
    let force = p5.Vector.sub(centerVec,this.position);
		let distance = force.mag()
		if (distance > 450) {
			force.setMag(0.1)
		} else {
			force.mult(0)
		}
    //let distanceSq = constrain(force.magSq(), 100, 1000);
    //let G = 5;
    //let strength = G * (this.mass * mover.mass) / distanceSq;
    //force.setMag(strength);
    //mover.applyForce(force);
		return force
  }

  flock(boids) {
    let alignment = this.align(boids);
    let cohesion = this.cohesion(boids);
    let separation = this.separation(boids);

		let centerForce = this.attractCenter();

    // alignment.mult(alignSlider.value());
    // cohesion.mult(cohesionSlider.value());
    // separation.mult(separationSlider.value());
		alignment.mult(alignSlider);
		cohesion.mult(cohesionSlider);
		separation.mult(separationSlider);

    this.acceleration.add(alignment);
    this.acceleration.add(cohesion);
    this.acceleration.add(separation);
	this.acceleration.add(centerForce)
  }

  update() {
    this.position.add(this.velocity);
    this.velocity.add(this.acceleration);
    this.velocity.limit(this.maxSpeed);
    this.acceleration.mult(0);
  }

  show(g, eyeG) {
		g.push()
		g.translate(this.position.x, this.position.y)
		g.rotate(this.velocity.heading());
    g.strokeWeight(6);

    //stroke(0);
		//point(0, 0);
		g.stroke(255)
		g.fill("yellow")
		//g.triangle(-this.r, -this.r / 2, -this.r, this.r / 2, this.r, 0);
		g.strokeWeight(90)
		g.point(0,0)
    //point(this.position.x, this.position.y);
		g.pop()

		eyeG.push()
			eyeG.noStroke()
			eyeG.translate(this.position.x, this.position.y)
			//eyeG.rotate(this.velocity.heading());
				eyeG.push()
					eyeG.translate(-15, -3)
					eyeG.rotate(PI/6)
					eyeG.fill(0)
					eyeG.arc(0,0,25,25,0,PI)
					// eyeG.fill(50)
					// eyeG.arc(0,0,15,15,0,PI)
					//eyeG.strokeWeight(10)
				eyeG.pop()


				eyeG.push()
					eyeG.translate(15, -3)
					eyeG.rotate(-PI/6)
					eyeG.fill(0)
					eyeG.arc(0,0,25,25,0,PI)
					// eyeG.fill(50)
					// eyeG.arc(0,0,15,15,0,PI)
					//eyeG.strokeWeight(10)
				eyeG.pop()

		eyeG.pop()
  }
}

/*
 * @name Applying Shaders as Textures
 * @description Shaders can be applied to 2D/3D shapes as textures.
 * To learn more about shaders and p5.js: https://itp-xstory.github.io/p5js-shaders/
 */

let theShader;
let webGLCanvas;
let originalGraphics;
let eyeGraphics;
const flock = [];

let attractor

let video;
let cacheGraphic
let videoW = 640
let videoH = 480
let poseNet;
let poses = [];
let windowCenter

function preload() {
  // load the shader
  theShader = loadShader("texture.vert", "texture.frag");
}

function setup() {
  // disables scaling for retina screens which can create inconsistent scaling between displays
  //pixelDensity(1);
  // shaders require WEBGL mode to work
  // createCanvas(1000, 1000);
  createCanvas(displayWidth, displayHeight);
  //noCursor()
  noStroke();

  webGLCanvas = createGraphics(width, height, WEBGL);
  originalGraphics = createGraphics(width, height);
  eyeGraphics = createGraphics(width, height);

  alignSlider = 2; //createSlider(0, 2, 1.5, 0.1);
  cohesionSlider = 0; //createSlider(0, 2, 1, 0.1);
  separationSlider = 2; //createSlider(0, 2, 2, 0.1);

  originalGraphics.noStroke();
  webGLCanvas.noStroke();
  noStroke();

  video = createCapture(VIDEO);
  video.size(videoW, videoH);

  cacheGraphic = createGraphics(videoW, videoH)
  cacheGraphic.translate(videoW,0)
  cacheGraphic.scale(-1,1)
  windowCenter = createVector(width/2, height/2)

  video.hide()

  // Create a new poseNet method with a single detection
  poseNet = ml5.poseNet(video, modelReady);
  // This sets up an event that fills the global variable "poses"
  // with an array every time new poses are detected
  poseNet.on('pose', function(results) {
    poses = results;
  });
  attractor = createVector(width/2, height/2)
  for (let i = 0; i < 40; i++) {
    flock.push(new Boid());
  }
}

function draw() {
  // here we're using setUniform() to send our uniform values to the shader
  theShader.setUniform("resolution", [width, height]);
  theShader.setUniform("time", millis() / 1000.0);
  theShader.setUniform("mouse", [mouseX, map(mouseY, 0, height, height, 0)]);

  webGLCanvas.shader(theShader);
  theShader.setUniform("u_resolution", [width / width, height / height]);
  theShader.setUniform("u_time", millis() / 1000);
  theShader.setUniform("u_mouse", [mouseX / width, mouseY / height]);
  theShader.setUniform("u_tex", originalGraphics);
  //console.log(mouseX/width)

  webGLCanvas.clear();
  webGLCanvas.rect(-width / 2, -height / 2, width, height);

  originalGraphics.background(0);
  eyeGraphics.clear();
  //drawPoseCenter()
  for (let boid of flock) {
    //boid.edges();
    boid.flock(flock, attractor);
    boid.update();
    boid.show(originalGraphics, eyeGraphics);
  }

  image(webGLCanvas, 0, 0);
  image(eyeGraphics, 0, 0);
  //image(originalGraphics,0,0)
  //image(eyeGraphics,0,0)
  // if (poses.length) {
  //   //cacheGraphic.image(video, 0, 0, videoW, videoH)
  //   image(cacheGraphic, 0, 0, videoW, videoH)
  // }

  cacheGraphic.clear()
  drawKeypoints();
  drawSkeleton();
  drawPoseCenter()
  image(cacheGraphic,0,0, width, height)
  push()
  fill(0,0,255)
  ellipse(attractor.x, attractor.y, 50,50)
  fill(255,0,0)
  ellipse(attractor.x, height/2, 20,20)
  pop()
  // push()
  // fill(0,0,255)
  // ellipse(attractor.x, height/2, 20,20)
  // pop()
}

