// Flocking
// Daniel Shiffman
// https://thecodingtrain.com/CodingChallenges/124-flocking-boids.html
// https://youtu.be/mhjuuHl6qHM

class Boid {
  constructor() {
    this.position = createVector(random(width), random(height));
    this.velocity = p5.Vector.random2D();
    this.velocity.setMag(random(2, 5));
    this.acceleration = createVector();
    this.maxForce = 0.2;
    this.maxSpeed = 3;
		this.r = 4;
  }

  edges() {
    if (this.position.x > width) {
      this.position.x = 0;
    } else if (this.position.x < 0) {
      this.position.x = width;
    }
    if (this.position.y > height) {
      this.position.y = 0;
    } else if (this.position.y < 0) {
      this.position.y = height;
    }
  }

  align(boids) {
    let perceptionRadius = 10//25;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        steering.add(other.velocity);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

  separation(boids) {
    let perceptionRadius = 60//24;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        let diff = p5.Vector.sub(this.position, other.position);
        diff.div(d * d);
        steering.add(diff);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

  cohesion(boids) {
    let perceptionRadius = 20//50;
    let steering = createVector();
    let total = 0;
    for (let other of boids) {
      let d = dist(this.position.x, this.position.y, other.position.x, other.position.y);
      if (other != this && d < perceptionRadius) {
        steering.add(other.position);
        total++;
      }
    }
    if (total > 0) {
      steering.div(total);
      steering.sub(this.position);
      steering.setMag(this.maxSpeed);
      steering.sub(this.velocity);
      steering.limit(this.maxForce);
    }
    return steering;
  }

	attractCenter(attractor) {
		const centerVec = attractor//acreateVector(attractor.x, height/2)
    let force = p5.Vector.sub(centerVec,this.position);
		let distance = force.mag()
		if (distance > 450) {
            this.maxSpeed = 10;
			force.setMag(0.1)
		} else {
            this.maxSpeed = 3;
			force.mult(0)
		}
    //let distanceSq = constrain(force.magSq(), 100, 1000);
    //let G = 5;
    //let strength = G * (this.mass * mover.mass) / distanceSq;
    //force.setMag(strength);
    //mover.applyForce(force);
		return force
  }

  flock(boids, attractor) {
    let alignment = this.align(boids);
    let cohesion = this.cohesion(boids);
    let separation = this.separation(boids);

		let centerForce = this.attractCenter(attractor);

    // alignment.mult(alignSlider.value());
    // cohesion.mult(cohesionSlider.value());
    // separation.mult(separationSlider.value());
		alignment.mult(alignSlider);
		cohesion.mult(cohesionSlider);
		separation.mult(separationSlider);

    this.acceleration.add(alignment);
    this.acceleration.add(cohesion);
    this.acceleration.add(separation);
	this.acceleration.add(centerForce)
  }

  update() {
    this.position.add(this.velocity);
    this.velocity.add(this.acceleration);
    this.velocity.limit(this.maxSpeed);
    this.acceleration.mult(0);
  }

  show(g, eyeG) {
		g.push()
		g.translate(this.position.x, this.position.y)
		g.rotate(this.velocity.heading());
    g.strokeWeight(6);

    //stroke(0);
		//point(0, 0);
		g.stroke(255)
		g.fill("yellow")
		//g.triangle(-this.r, -this.r / 2, -this.r, this.r / 2, this.r, 0);
		g.strokeWeight(90)
		g.point(0,0)
    //point(this.position.x, this.position.y);
		g.pop()

		eyeG.push()
			eyeG.noStroke()
			eyeG.translate(this.position.x, this.position.y)
			//eyeG.rotate(this.velocity.heading());
				eyeG.push()
					eyeG.translate(-15, -3)
					eyeG.rotate(PI/6)
					eyeG.fill(0)
					eyeG.arc(0,0,25,25,0,PI)
					// eyeG.fill(50)
					// eyeG.arc(0,0,15,15,0,PI)
					//eyeG.strokeWeight(10)
				eyeG.pop()


				eyeG.push()
					eyeG.translate(15, -3)
					eyeG.rotate(-PI/6)
					eyeG.fill(0)
					eyeG.arc(0,0,25,25,0,PI)
					// eyeG.fill(50)
					// eyeG.arc(0,0,15,15,0,PI)
					//eyeG.strokeWeight(10)
				eyeG.pop()

		eyeG.pop()
  }
}

