class Particle extends VerletParticle2D {
  constructor(x, y) {
    super(x, y);
    this.r = 2;
    physics.addParticle(this);
  }

  show(g) {
    g.fill(252, 238, 33);
    g.strokeWeight(1);
    g.circle(this.x, this.y, this.r * 12);

    g.strokeWeight(this.r * 4);
    g.point(this.x, this.y);
  }
}

class Spring extends VerletSpring2D {
  constructor(a, b, strength) {
    let length = dist(a.x, a.y, b.x, b.y);
    super(a, b, length * 1, 0.001);
    physics.addSpring(this);
  }

  show() {
    strokeWeight(1);
    stroke(0, 127);
    line(this.a.x, this.a.y, this.b.x, this.b.y);
  }
}

// attribute is the variable type
// vec3 is the data type
attribute vec3 aPosition;

attribute vec2 aTexCoord;

varying vec2 vTexCoord;

void main(){

  vTexCoord = aTexCoord;

  // vec4 holds 4 components xyzw or rgba or stuv
  vec4 position = vec4(aPosition.x, aPosition.y, aPosition.z, 1.0);

  position.xy = position.xy * 2.0 - 1.0;

  // Output the position from the shader
  gl_Position = position;
}

// Set the precision
precision highp float;

uniform vec2 mouse;
uniform sampler2D tex;

varying vec2 vTexCoord;

void main() {

  vec2 uv = vTexCoord;
  // Flip the texture coords
  uv.y = 1.0 - uv.y;

  vec2 steps = vec2(200.0, 200.0) * mouse;

  vec2 pixelUv = floor( uv * steps ) / steps;

  vec4 color = texture2D(tex, pixelUv);

  gl_FragColor = color;
}

